import React from 'react';
import axios from '../../axios-orders';

const api = () => {
    const fetchAPI = () => {
        let info = null
        info = axios.get('https://portal-tech-react-default-rtdb.firebaseio.com/requestCompany.json')
            .then(response => console.log(response))
            .catch(error => console.log(error));
        return info.data
    }

    return(
        fetchAPI()
    )
};

export default api;
