import React, { Component } from 'react';

import Container from '../../hoc/Container/Container';
import Image from '../Decoration/Borders/Images/Image/Image';
import styles from './Footer.module.css';
// import Logo from '../Logo/Logo';
import Pnglogo from '../../assets/img/portal-tech.png'

class Footer extends Component {
    render () {
        return (
            <footer>
                <Container>
                    <div className={styles.InnerContainer}>
                        <div style={{display: 'inline-block'}}>
                            <div className={styles.FooterElement} style={{display: 'inline-block', maxWidth: '33%'}}>
                                <img src={Pnglogo} style={{width: '150px'}}></img>
                                {/* <Image src={Pnglogo} style={{width: '150px', margin: '0 10px'}}/> */}
                            </div>
                        </div>
                        <div className={styles.FooterElement} style={{display: 'inline-block', maxWidth: '33%', verticalAlign: 'top', margin: '0 60px'}}>
                            <p style={{color: 'white', fontWeight: '500', margin: 0}}>Есть вопросы? Звоните по телефону:<br/><span>+7 (913) 122-28-02</span><br/><span>+7 (916) 716-33-00</span></p>
                        </div>
                        <div style={{textAlign: 'center', color: 'white'}}>
                            <p>@2021 Portal Tech</p>
                        </div>
                    </div>
                </Container>
            </footer>
        )
    }
};

export default Footer;
