import React, { Component } from 'react';

import Container from '../../hoc/Container/Container';
import RequestForm from "../Requests/RequestForm/RequestForm";
import styles from './Requests.module.css';

class Requests extends Component {
    render () {
        return (
            <div className={styles.Requests}>
                <Container>
                    <h1 style={{color: 'white'}}>Подать заявку</h1>
                    <div style={{width: '70%', textAlign: 'center', margin: '0 auto', color: 'white', fontWeight: '600', fontSize: '22px', paddingBottom: '20px'}}>
                        <p>У вас интересный проект, что ищет клиентов? Или вы являетесь предпринимателем и желаете найти себе нужный вам проект? Оставьте свои контакты ниже и мы с вами обязательно свяжемся!</p>
                    </div>
                    <RequestForm text='Желаете подобрать себе разработчика, либо их готовое решение? Оставьте заявку здесь!'/>
                    <RequestForm text='Вы разработчик и хотите получать заявки на разработку? Оставьте заявку здесь!'/>
                </Container>
            </div>
        )
    }
};

export default Requests;
