import React from 'react';

import styles from './RequestForm.module.css';
import Container from '../../../hoc/Container/Container';
import axios from '../../../axios-orders';

const requestForm = props => {
    return (
        <div className={styles.RequestForm} style={{backgroundImage: props.bgimage}}>
            <Container>
                <div className={styles.InnerContainer}>
                    <p>{props.text}</p>
                    <form method='get'>
                        <div className='Form'>
                            <input type='text' name='name' id='name' placeholder='Ваше имя' required />
                        </div>
                        <div className='Form'>
                            <input type='text' name='name' id='company' placeholder="Название проекта/компании" required />
                        </div>
                        <div className='Form'>
                            <input type='email' name='name' id='email' placeholder="Ваш номер телефона" required />
                        </div>
                        <div className='Form'>
                            <input type='submit' className={styles.Submit} />
                        </div>
                    </form>
                </div>
            </Container>
        </div>
    )
};

export default requestForm;
