import React from 'react';
import axios from '../../axios-orders';

const api = () => {
    const fetchAPI = () => {
        let data = null
        axios.get('https://portal-tech-react-default-rtdb.firebaseio.com/requestCompany.json')
            .then(response => data = response.data)
            .catch(error => console.log(error));
        return data
    }

    return(
        fetchAPI()
    )
};

export default api;
