import React, { Component } from 'react';

import Container from '../../hoc/Container/Container';
import Image from '../Decoration/Borders/Images/Image/Image';
import styles from './Footer.css';
// import Logo from '../Logo/Logo';
import Pnglogo from '../../assets/img/portal-tech.png'

class Footer extends Component {
    render () {
        return (
            <footer>
                <Container>
                    <div className={styles.InnerContainer}>
                        <div className={styles.FooterElement}>
                            <Image src={Pnglogo} width='150px'/>
                        </div>
                        <div className={styles.FooterElement}>
                            <p style={{color: 'white', fontWeight: '500'}}>Есть вопросы? Звоните по телефону:<br/><span>+7 (913) 122-28-02</span><br/><span>+7 (916) 716-33-00</span></p>
                        </div>
                    </div>
                </Container>
            </footer>
        )
    }
};

export default Footer;
