import React, { Component } from 'react';

import styles from './RequestForm.module.css';
import Container from '../../../hoc/Container/Container';
import axios from '../../../axios-orders';

class RequestForm extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            name: '',
            company: '',
            phone: '',  
            developer: '',  
        };
      }
    
    sendCompanyFormData = () => {
        const request = {
            name: this.state.name,
            company: this.state.company,
            tel: this.state.phone,
        }
        axios.post('/requestCompany', request);
    }
    
    sendDevFormData = () => {
        const request = {
            name: this.state.name,
            company: this.state.developer,
            tel: this.state.phone,
        }
        axios.post('/requestDev', request);
    }

      handleChange = (event) => {
        this.setState({[event.target.name]: event.target.value});
      }
    
      handleSubmit = (event) => {
        alert('A form was submitted: ' + this.state);
    
        axios.post('', {
            method: 'POST',
            // We convert the React state to JSON and send it as the POST body
            body: JSON.stringify(this.state)
          }).then(function(response) {
            console.log(response)
            return response.json();
          });
    
        event.preventDefault();
    }

    render () {
        if (this.props.formType == 'company') {
            return (
                <div className={styles.RequestForm} style={{backgroundImage: props.bgimage}}>
                    <Container>
                        <div className={styles.InnerContainer}>
                            <p>{props.text}</p>
                            <form method='get'>
                                <div className='Form'>
                                    <input type='text' value={this.state.name} name='name' id='name' placeholder='Ваше имя' required />
                                </div>
                                <div className='Form'>
                                    <input type='text' value={this.state.company} name='company' id='company' placeholder="Название проекта/компании" required />
                                </div>
                                <div className='Form'>
                                    <input type='text' value={this.state.phone} name='phone' id='phone' placeholder="Ваш номер телефона" required />
                                </div>
                                <div className='Form'>
                                    <input type='submit' className={styles.Submit} />
                                </div>
                            </form>
                        </div>
                    </Container>
                </div>
            )
        }
        else if (this.props.formType == 'dev') {
            return (
                <div className={styles.RequestForm} style={{backgroundImage: props.bgimage}}>
                    <Container>
                        <div className={styles.InnerContainer}>
                            <p>{props.text}</p>
                            <form method='get'>
                                <div className='Form'>
                                    <input type='text' value={this.state.name} name='name' id='name' placeholder='Ваше имя' required />
                                </div>
                                <div className='Form'>
                                    <input type='text' value={this.state.developer} name='developer' id='company' placeholder="Название проекта/компании" required />
                                </div>
                                <div className='Form'>
                                    <input type='text' value={this.state.phone} name='phone' id='phone' placeholder="Ваш номер телефона" required />
                                </div>
                                <div className='Form'>
                                    <input type='submit' className={styles.Submit} />
                                </div>
                            </form>
                        </div>
                    </Container>
                </div>
            )
        }
        else {
            return null
        }
    }    
};

export default RequestForm;
